import React, { Component } from 'react'
import './App.css'
import TodoList from './Components/TodoList'

export class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
       items : [],
       currentItem : {
         text : '', 
         Key: ''
        }
    }
  }
  handleInput = e => {
    const itemText = e.target.value
    const currentItem =  
    {
      text : itemText,
      key : Date.now ()
    }
    this.setState ({
      currentItem,
    })
  } 
  addItem = e => {
    e.preventDefault()
    console.log("hello item added")
  }

  render() {
    return (
      <div className ="App">
        <TodoList addItem = {this.addItem}
        inputElement = {this.inputElement}
        handleInput = {this.handleInput} 
        currentItem = {this.currentItem}/>
      </div>
    )
  }
}

export default App
