import React, {Component} from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import TodoInput from './components/todoInput'

class  App extends Component {
  state = {
    id: Date.now(),
    item: "",
    items: [],
    editItem: false
  };  
  onChange = e => {
    this.setState({
      item: e.target.value
    });
  };
  onSubmit = e => {
    e.preventDefault();

    const newItem = {
      id: this.state.id,
      title: this.state.item
    };
    const updatedItems = [...this.state.items, newItem];

    this.setState({
      items: updatedItems,
      item: "",
      id: Date.now(),
      editItem: false
    });
  };
  render () {
  return (
        <div className="App">

    </div>
  );
  }
}

export default App;
