import React, { Component } from 'react'
import './App.css'
import TodoList from './Components/TodoList'

export class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
       items : [],
       currentItem : {
         text : '', 
         Key: ''
        }
    }
  }
  handleInput = e => {
    console.log('hello WOrld')
  } 
  addItem = e => {
    e.preventDefault()
    console.log("hello item added")
  }

  render() {
    return (
      <div className ="App">
        <TodoList addItem = {this.addItem} />
      </div>
    )
  }
}

export default App
